import React, { useState, useEffect } from "react";

const Charaters = props => {
  const myDate = new Date();
  const date = () => {
    let newDate = new Date().toLocaleDateString("fr-FR", {
      month: "numeric",
      day: "numeric",
      year: "numeric"
    });
    return newDate;
  };
  console.log(date(myDate));

  return (
    <main>
      (
      <div className="container-fluide ">
        <div className="row">
          <div className="blocName col-xl-2  text-dark   ">
            {props.data.map(user => (
              <div className="card  text-dark" style={{ width: "470px" }}>
                <img src={user.img} className="card-img-top w-auto" alt="..." />
                <div className="card-body" key={user.char_id}>
                  <h5 id={user.name}>{user.name}</h5>
                  <p className="card-text">Birthday: {user.birthday}</p>
                  <p className="card-text">Occupation: {user.occupation}</p>
                  <p className="bg-success text-center text-light fw-bolder py-3">
                    {user.status === "Alive"
                      ? (document.body.style.background = "red")
                      : (document.body.style.background = "red")}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      )
    </main>
  );
};

export default Charaters;
