import React, { useState, useEffect } from "react";

const Charaters = props => {
  const myDate = new Date();
  const date = () => {
    let newDate = new Date().toLocaleDateString("fr-FR", {
      month: "numeric",
      day: "numeric",
      year: "numeric"
    });
    return newDate;
  };
  console.log(date(myDate));
  let bgDeathOrAlive = document.querySelector(".bgDeathOrAlive");

  let myStatus =
    "bgDeathOrAlive bg-success text-center text-light fw-bolder py-3";

  let myStatusDeath =
    "bgDeathOrAlive bg-danger text-center text-light fw-bolder py-3";

  window.addEventListener("click", e => {
    console.log(e.target);
  });
  return (
    <main>
      (
      <div className="container-fluide ">
        <div className="row">
          <div className="blocName col-xl-2  text-dark   ">
            {props.data.map(
              user => {
                switch (user.occupation) {
                  case "Deceased":
                    return <p className={myStatus}>{user.status}</p>;
                    break;

                  case "Alive":
                    return <p className={myStatusDeath}>{user.status}</p>;
                    break;
                }
              },
              <div className="card  text-dark" style={{ width: "470px" }}>
                <img src={user.img} className="card-img-top w-auto" alt="..." />
                <div className="card-body" key={user.char_id}>
                  <h5 id={user.name}>{user.name}</h5>
                  <p className="card-text">Birthday: {user.birthday}</p>}
                  <p className="card-text">Occupation: {user.occupation}</p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
      )
    </main>
  );
};

export default Charaters;
