import React, { useEffect, useState } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";

const Cards = () => {
  const [data, setData] = useState([]);
  useEffect(() => {
    axios
      .get("https://breakingbadapi.com/api/characters")
      .then(res => setData(res.data));
  }, []);
  return (
    <div
      className="MyCards "
      style={{
        background: "url('./img/walter.png')",
        backgroundRepeat: "no-repeat",

        height: "100vh",
        backgroundSize: "cover"
      }}
    >
      <div>
        <img src="./img/logo.png" alt="walter" className=" h-auto" />
      </div>

      <div className="blocName d-flex flex-column text-light ">
        {data.map(user => (
          <NavLink to={user.name} className="">
            <span className="trait" style={{ height: auto, width: "20px" }}>
              Â 
            </span>
            <h1 id={user.name} key={user.char_id}>
              {" "}
              {user.name}
            </h1>
          </NavLink>
        ))}
      </div>
    </div>
  );
};

export default Cards;
